import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";

const Connection = () => {

  const [chartData, setChartData] = useState({});
  //const [i_b, seti_b] = useState([]);
  //const [v_bc, setv_bc] = useState([]);

  const chart = () => {
    let current_a = [];
    let current_b = [];
    let current_c = [];
    let current_n = [];
    let timestamp = [];
    axios
      .get("http://localhost:3000/register_values_em6400")
      .then(res => {
        //console.log(res);
        for (const dataObj of res.data) {
          current_a.push(parseInt(dataObj.i_a));
          current_b.push(parseInt(dataObj.i_b));
          current_c.push(parseInt(dataObj.i_c));
          current_n.push(parseInt(dataObj.i_avg));
          timestamp.push(parseInt(dataObj.timestamp));
        }
        setChartData({
          labels:timestamp,
          datasets: [
            {
              label: "i_a vs time",
              data: current_a,
              fill: false,
              backgroundColor: "rgba(75,192,192,0.2)",
              borderColor: "rgba(75,192,192,1)",
              borderWidth: 4
            },
            {
              label: "i_b vs time",
              data: current_b,

              borderColor:"orange",
              backgroundColor:"orange",
              borderWidth: 4
            },
            {
              label: "i_c vs time",
              data: current_c,
              fill:false,
              borderColor: "#742774",
              backgroundColor: "#742774",
              borderWidth: 4
            },
            {
              label: "i_avg vs time",
              data: current_n,
              fill:false,
              borderColor:"green",
              borderWidth: 4
            }
          ]
        });
      })
      .catch(err => {
        console.log(err);
      });
    //console.log(current2, voltage2);
  };

  useEffect(() => {
    chart();
  }, []);
  return (
    <div className="Appline">
      <h1>Current_vs_Timestamp</h1>
      <div>
        <Line data={chartData}
          options={{
            responsive: true,
            title: { text: "THICCNESS SCALE", display: true },
            scales: {
              yAxes: [
                {
                  ticks: {
                    autoSkip: true,
                    maxTicksLimit: 10,
                    beginAtZero: true
                  },
                  gridLines: {
                    display: false
                  }
                }
              ],
              xAxes: [
                {
                  gridLines: {
                    display: false
                  }
                }
              ]
            }
          }}
        />
      </div>
    </div>
  );
};

export default Connection;
