import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";

const Connection = () => {

  const [chartData, setChartData] = useState({});
  const [i_b, seti_b] = useState([]);
  const [v_bc, setv_bc] = useState([]);

  const chart = () => {
    let current = [];
    let voltage = [];
    axios
      .get("http://localhost:3000/register_values_em6400")
      .then(res => {
        console.log(res);
        for (const dataObj of res.data) {
          current.push(parseInt(dataObj.i_a));
          voltage.push(parseInt(dataObj.v_ab));
        }
        setChartData({
          labels: voltage,
          datasets: [
            {
              label: "voltage vs current",
              data: current,
              backgroundColor: ["rgba(75, 19, 192, 0.6)"],
              borderWidth: 4
            }
          ]
        });
      })
      .catch(err => {
        console.log(err);
      });
    console.log(current, voltage);
  };

  useEffect(() => {
    chart();
  }, []);
  return (
    <div className="Appline">
      <h1>LineGraph</h1>
      <div>
        <Line
          data={chartData}
          options={{
            responsive: true,
            title: { text: "THICCNESS SCALE", display: true },
            scales: {
              yAxes: [
                {
                  ticks: {
                    autoSkip: true,
                    maxTicksLimit: 10,
                    beginAtZero: true
                  },
                  gridLines: {
                    display: false
                  }
                }
              ],
              xAxes: [
                {
                  gridLines: {
                    display: false
                  }
                }
              ]
            }
          }}
        />
      </div>
    </div>
  );
};

export default Connection;
