import {Bar} from 'react-chartjs-2';
import axios from "axios";

const Connection2 = () => {

    const [chartData, setChartData] = useState({});
    const [i_b, seti_b] = useState([]);
    const [v_bc, setv_bc] = useState([]);

    const chart = () => {
      let current2 = [];
      let voltage2 = [];
      axios
        .get("http://localhost:3000/register_values_em6400")
        .then(res => {
          console.log(res);
          for (const dataObj of res.data) {
            current2.push(parseInt(dataObj.i_b));
            voltage2.push(parseInt(dataObj.v_bc));
          }
          setChartData({
            labels: voltage2,
            datasets: [
              {
                label: "voltage vs current",
                data: current2,
                backgroundColor: ["rgba(75, 19, 192, 0.6)"],
                borderWidth: 4
              }
            ]
          });
        })
        .catch(err => {
          console.log(err);
        });
      console.log(current2, voltage2);
    };

