import React, { Component,useEffect,useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "../node_modules/react-vis/dist/style.css";
//import Connection from './Components/Connection.js';
import Connection1 from './Connection1.js';
import Connection from './Connection.js';
import Connection2 from './Connection2.js';
import axios from "axios";



//import D3FlareData from '../datasets/d3-flare-example.json';
import "./App.css";
//import Home from "./Home";
import Header from "./Header";
import {
  Hint,
  XYPlot,
  LineSeries,
  VerticalGridLines,
  HorizontalGridLines,
  XAxis,
  YAxis,
  VerticalBarSeries,
  HorizontalBarSeries,
  HeatmapSeries,
  RadialChart,

} from "react-vis";




// all imports are over by here


class App extends Component {

    state = {
    value:false,
    data : []
  };

   inputdata = () => {


  axios
        .get("http://localhost:3000/register_values_em6400")
        .then(res => {
          //console.log(res);
          let data1=[];
          for (const dataObj of res.data) {
            let temp = {x:"",y:""}
            temp.x= parseInt(dataObj.i_b);
            temp.y = parseInt(dataObj.v_bc);
            data1.push(temp);
          };
          this.setState({data:data1,value:false});
          console.log(data1);

        })
        .catch(err => {
          console.log(err);
        });



    // useEffect(() => {
    //   chart();
    // }, []);
  }
  componentDidMount = ()=>{
    this.inputdata();
  }

  render() {
    const { value } = this.state;
    const myData = [{ angle: 1 }, { angle: 5 }, { angle: 2 }];

    // //const data = [
    //   // { x: 1, y: 0, color: 10 },
    //   { x: 1, y: 5, color: 10 },
    //   { x: 1, y: 10, color: 6 },
    //   { x: 1, y: 15, color: 7 },
    //   { x: 2, y: 0, color: 12 },
    //   { x: 2, y: 5, color: 2 },
    //   { x: 2, y: 10, color: 1 },
    //   { x: 2, y: 15, color: 12 },
    //   { x: 3, y: 0, color: 9 },
    //   { x: 3, y: 5, color: 2 },
    //   { x: 3, y: 10, color: 6 },
    //   { x: 3, y: 15, color: 12 },
    // ];


    return (
      <div className="App" style={{ backgroundColor: "" }}>


              <Header />
              <XYPlot height={300} width={700}>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <LineSeries data={this.state.data} />
              </XYPlot>

              <XYPlot height={300} width={2000}>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <VerticalBarSeries data={this.state.data} />
              </XYPlot>

              <XYPlot height={300} width={2000}>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <HorizontalBarSeries data={this.state.data} />
              </XYPlot>

               <XYPlot height={400} width={2000}>
                <XAxis />
                <YAxis />
                {}
                <HeatmapSeries
                  className="heatmap-series-example"
                  //onValueMouseOver={(v) => this.setState({ value: v })}
                  //onSeriesMouseOut={(v) => this.setState({ value: false })}
                  data={myData}
                />

              </XYPlot> 

              <RadialChart data={myData} width={400} height={400} />

              <Connection1 />
              <Connection />
              <Connection2 />



      </div>
    );
  }
}
export default App;
