import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "../node_modules/react-vis/dist/style.css";
import "./App.css";
import Home from "./Home";
import Header from "./Header";
import { Hint, XYPlot, LineSeries,VerticalGridLines,HorizontalGridLines,XAxis, YAxis, VerticalBarSeries, HorizontalBarSeries, HeatmapSeries } from "react-vis";

class App extends Component {
  state={
    value:false
  }
  render() {
    const{value} = this.state;
    const data = [

    ];
    return (
      <div className="App" style={{ backgroundColor: "" }}>
        <Router>
          <Switch>
            <Route path="/">
              <Header />
              <XYPlot height={300} width={300}>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <LineSeries data={data} /></XYPlot>

                <XYPlot height={300} width={300} color='green'>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <VerticalBarSeries data={data} /></XYPlot>

                <XYPlot height={300} width={300} color='magenta'>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <HorizontalBarSeries data={data} />
              </XYPlot>

              <XYPlot height={300} width={300}>
                <XAxis />
                <YAxis />
                <HeatmapSeries className="heatmap-series-example" 
                onValueMouseOver={v => this.setState({value:v})}
                onSeriesMouseOut={v => this.setState({value:false})} data={data}/>
                {value !== false && <Hint value={value}/>}
              </XYPlot>
            </Route>
          </Switch>
        </Router>
      </div>
    );
  }
}
export default App;
