import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "../node_modules/react-vis/dist/style.css";
import "./App.css";
import Home from "./Home";
import Header from "./Header";
import { Hint, 
         XYPlot,
          LineSeries,
          VerticalGridLines,
          HorizontalGridLines,
          XAxis, YAxis,
           VerticalBarSeries,
          HorizontalBarSeries,
           HeatmapSeries,
          RadialChart,Treemap } from "react-vis";
const MODE = [
  'circlePack',
  'partition',
  'partition-pivot',
  'squarify',
  'resquarify',
  'slice',
  'dice',
  'slicedice',
  'binary'
];

const STYLES = {
  SVG: {
    stroke: '#ddd',
    strokeWidth:'0.25',
    strokeOpacity:0.5
  },
  DOM:{
    border: 'thin solid #ddd'
  }
};

class App extends Component {
  state1 = {
    modeIndex:0,
    useSVG:true
  };
  state={
    value:false
  };

  updateModeIndex = increment => () => {
    const newIndex = this.state.modeIndex + (increment?1:-1);
    const modeIndex = newIndex <0 ?MODE.length - 1:newIndex >= MODE.length ? 0:newIndex;
    this.setState({modeIndex});
  };
  render() {
    const{value} = this.state;
    const myData = [{angle:1},{angle:5},{angle:2}]
    const {modeIndex, useSVG} = this.state; 
    const data = [
      {x: 1, y: 0, color: 10},
      {x: 1, y: 5, color: 10},
      {x: 1, y: 10, color: 6},
      {x: 1, y: 15, color: 7},
      {x: 2, y: 0, color: 12},
      {x: 2, y: 5, color: 2},
      {x: 2, y: 10, color: 1},
      {x: 2, y: 15, color: 12},
      {x: 3, y: 0, color: 9},
      {x: 3, y: 5, color: 2},
      {x: 3, y: 10, color: 6},
      {x: 3, y: 15, color: 12}
    ];
    return (
      <div className="App" style={{ backgroundColor: "" }}>
        <Router>
          <Switch>
            <Route path="/">
              <Header />
              <XYPlot height={300} width={700}>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <LineSeries data={data} /></XYPlot>

                <XYPlot height={300} width={700}>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <VerticalBarSeries data={data} /></XYPlot>

                <XYPlot height={300} width={700}>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <HorizontalBarSeries data={data} />
              </XYPlot>

              <XYPlot height={400} width={700}>
                <XAxis />
                <YAxis />
                <HeatmapSeries className="heatmap-series-example" 
                onValueMouseOver={v => this.setState({value:v})}
                onSeriesMouseOut={v => this.setState({value:false})} data={data}/>
                {value !== false && <Hint value={value}/>}
              </XYPlot>

              <RadialChart data={myData} width={400} height={400} />

              <div className="centered-and-flexed">
                <div className="centered-and-flexed-controls">
                  <ShowcaseButton
                  onClick = {()=> this.setState({useSVG:!useSVG})}
                  buttonContent={useSVG ? 'USE DOM': 'USE SVG'} />
                </div>
                <div className="centered-and-flexed-controls">
                  <ShowcaseButton
                  onClick={this.updateModeIndex(false)}
                  buttonContent={'PREV MODE'} />
                <div>{MODE[modeIndex]}</div>
                <ShowcaseButton
                onClick={this.updateModeIndex(true)}
                buttonContent={'NEXT MODE'}/>
                </div>
              </div>


            </Route>
          </Switch>
        </Router>
      </div>
    );
  }
}
export default App;
